import React from "react";
import { Dropdown } from "react-native-element-dropdown";
import AntDesign from "@expo/vector-icons/AntDesign";
import { View, Text, StyleSheet, TextInput } from "react-native";

const EditView = (props) => {
  const [value, setValue] = React.useState(null);
  const [isFocus, setIsFocus] = React.useState(false);

  const waiters = [
    { value: "1", label: "Missim ", emoji: "ðŸ˜€" },
    { value: "2", label: "Anca", emoji: "ðŸ˜€" },
    { value: "3", label: "Dilara", emoji: "ðŸ˜€" },
    { value: "3", label: "Aldis", emoji: "ðŸ˜€" },
  ];

  const { label } = props;
  const [bargeld, onChangeBargeld] = React.useState("");
  const [voucher, onChangeVoucher] = React.useState("");
  const [cards, onChangeCards] = React.useState("");
  return (
    <View style={styles.container}>
      <Dropdown
        style={[styles.dropdown, isFocus && { borderColor: "blue" }]}
        placeholderStyle={styles.placeholderStyle}
        selectedTextStyle={styles.selectedTextStyle}
        inputSearchStyle={styles.inputSearchStyle}
        iconStyle={styles.iconStyle}
        data={waiters}
        search
        maxHeight={300}
        labelField="label"
        valueField="value"
        emojiField="emoji"
        placeholder={!isFocus ? "Select Waiter" : "..."}
        searchPlaceholder="Search..."
        value={value}
        onFocus={() => setIsFocus(true)}
        onBlur={() => setIsFocus(false)}
        onChange={(item) => {
          setValue(item.value);
          setIsFocus(false);
        }}
        renderLeftIcon={() => (
          <AntDesign
            style={styles.icon}
            color={isFocus ? "blue" : "black"}
            name="UserOutlined"
            size={20}
          />
        )}
      />
      <Text style={styles.text}>{label}</Text>
      <TextInput
        style={styles.input}
        onChangeText={onChangeBargeld}
        value={bargeld}
        placeholder="Bar_gegeben"
        keyboardType="numeric"
      />
      <TextInput
        style={styles.input}
        onChangeText={onChangeVoucher}
        value={voucher}
        placeholder="Gutscheine"
        keyboardType="numeric"
      />
      <TextInput
        style={styles.input}
        onChangeText={onChangeCards}
        value={cards}
        placeholder="Karten"
        keyboardType="numeric"
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#333",
    width: "100%",
  },
  text: { color: "white" },
  input: {
    height: 25,
    color: "white",
    borderWidth: 0.5,
    borderRadius: 8,
    borderColor: "orangered",
  },
  dropdown: {
    height: 40,
    width: 200,
    borderColor: "gray",
    borderWidth: 0.5,
    borderRadius: 8,
    paddingHorizontal: 8,
  },
  icon: {
    marginRight: 5,
  },
  label: {
    position: "absolute",
    backgroundColor: "white",
    left: 22,
    top: 8,
    zIndex: 999,
    paddingHorizontal: 8,
    fontSize: 14,
  },
  placeholderStyle: {
    fontSize: 16,
  },
  selectedTextStyle: {
    fontSize: 16,
  },
  iconStyle: {
    width: 20,
    height: 20,
  },
  inputSearchStyle: {
    height: 40,
    fontSize: 16,
  },
});

export default EditView;
