import React from "react";
import { Dropdown } from "react-native-element-dropdown";
import {
	View,
	Text,
	StyleSheet,
	TextInput,
} from 'react-native';

const EditView = (props) => {
	const { value } = props;
	const [bargeld, onChangeBargeld] = React.useState('');
	const [voucher, onChangeVoucher] = React.useState('');
	const [cards, onChangeCards] = React.useState('');
	return (

		<View style={styles.container}>
			<Text style={styles.text}>{value}</Text>
			<TextInput
				style={styles.input}
				onChangeText={onChangeBargeld}
				value={bargeld}
				placeholder="Bar_gegeben"
				keyboardType="numeric"
			/>
			<TextInput
				style={styles.input}
				onChangeText={onChangeVoucher}
				value={voucher}
				placeholder="Gutscheine"
				keyboardType="numeric"
			/>
			<TextInput
				style={styles.input}
				onChangeText={onChangeCards}
				value={cards}
				placeholder="Karten"
				keyboardType="numeric"
			/>
		</View>


	)
};

const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: 'black',
		width: '100%',
	},
	text: { color: 'white' },
	input: {
		height:25,
		color: 'white',
		borderWidth: 0.5,
		borderRadius: 8,
		borderColor: 'orangered',
	},

})


export default EditView;
